package Assignment;
// Program 37 to show the difference between print() and println()
public class Assignment37_Print_and_Println {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        System.out.println("Printing using Println");
		System.out.println("1");
		System.out.println("2");
		System.out.println("Printing using Print");
		System.out.print("1");
		System.out.print("2");
	}

}
